import React, { useState } from 'react';
import ReportPreview from '../components/ReportPreview';

// import jsPDF from 'jspdf';
// import html2canvas from 'html2canvas';

import domtoimage from 'dom-to-image';
import { jsPDF } from 'jspdf';

import '../styles/ReportPage.scss';

const ReportPage = () => {
  const [template, setTemplate] = useState('template1');
  const [title, setTitle] = useState('');
  const [duration, setDuration] = useState('');
  const [location, setLocation] = useState('');
  const [pictures, setPictures] = useState([]);

  const handleGeneratePdf = () => {
    const reportElement = document.querySelector('.report-preview');

    if (!reportElement) {
      alert('No report to generate.');
      return;
    }

    html2canvas(reportElement)
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/png');

        const pdf = new jsPDF('p', 'mm', 'a4'); // A4 size page

        const pdfWidth = pdf.internal.pageSize.getWidth();
        const pdfHeight = pdf.internal.pageSize.getHeight();

        // Get canvas dimensions
        const canvasWidth = canvas.width;
        const canvasHeight = canvas.height;

        // Calculate the aspect ratio of the canvas
        const aspectRatio = canvasWidth / canvasHeight;

        // Calculate new dimensions while maintaining the aspect ratio
        let imgWidth = pdfWidth;
        let imgHeight = pdfWidth / aspectRatio;

        // If the image height is still larger than PDF height, adjust the height and width again
        if (imgHeight > pdfHeight) {
          imgHeight = pdfHeight;
          imgWidth = pdfHeight * aspectRatio;
        }

        // Add image to the PDF (centered if not a perfect fit)
        const xOffset = (pdfWidth - imgWidth) / 2;
        const yOffset = (pdfHeight - imgHeight) / 2;

        pdf.addImage(imgData, 'PNG', xOffset, yOffset, imgWidth, imgHeight);
        pdf.save('report.pdf');
      })
      .catch((error) => {
        console.error('Error generating PDF:', error);
      });
  };

  return (
    <div className="report-page">
      <h1>Generate Report</h1>
      <form className="report-form">
        <label>
          Template:
          <select value={template} onChange={(e) => setTemplate(e.target.value)}>
            <option value="template1">Template 1</option>
            <option value="template2">Template 2</option>
          </select>
        </label>

        <label>
          Title:
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </label>

        <label>
          Duration:
          <input type="text" value={duration} onChange={(e) => setDuration(e.target.value)} />
        </label>

        <label>
          Location:
          <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} />
        </label>

        <label>
          Pictures (URLs separated by commas):
          <input
            type="text"
            value={pictures}
            onChange={(e) => setPictures(e.target.value.split(','))}
          />
        </label>

        <button type="button" onClick={handleGeneratePdf}>Download PDF</button>
      </form>

      <ReportPreview
        template={template}
        title={title}
        duration={duration}
        location={location}
        pictures={pictures}
      />
    </div>
  );
};

export default ReportPage;
